function getCookie(name) {
  return document.cookie.split("; ").find(row => row.startsWith(name + "=")) ? true : false;
}

function setCookie(name, value) {
  document.cookie = `${name}=${value}; path=/; SameSite=None; Secure`;
}

function blockUser() {
  return true;
}

